import React, { useState, useEffect, useContext } from "react";
import { useParams } from "react-router-dom";
import BooklyAPI from "./api";
// import BookCard from "./BookCard"; //Decided against re-using card so could customize it more
import DOMPurify from "dompurify"; //THIS Is to clean up the source code, get rid of the raw HTML that the API was giving
import UserContext from "./UserContext";
// import "./BookDetail.css";
import axios from "axios";

function BookDetail() {
  const { token } = useContext(UserContext); // Get token from UserContext

  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isFavorite, setIsFavorite] = useState(false); // track if the book is already in favorites
  const { currentUser } = useContext(UserContext);

  useEffect(() => {
    async function fetchBook() {
      const singleBook = await BooklyAPI.getSingleBook(id);
      setBook(singleBook.volumeInfo);
    }
    fetchBook();
    setIsLoading(false);
  }, [id]);

  // Check if the book is already in the user's favorites when the component loads
  useEffect(() => {
    if (currentUser) {
      async function checkIfFavorite() {
        try {
          const response = await axios.get(
            `http://localhost:3001/bookFavorites/${id}`, // Updated API endpoint with the book ID
            {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            }
          );

          setIsFavorite(response.data.isFavorite); // Update based on server response
        } catch (err) {
          console.error("Error checking favorites:", err);
        }
      }
      checkIfFavorite();
    }
  }, [id, currentUser, token]);

  const handleFavoriteClick = async () => {
    if (!currentUser) {
      alert("You need to be logged in to add favorites.");
      return;
    }

    try {
      if (isFavorite) {
        // Remove the book from favorites
        await axios.delete(`http://localhost:3001/bookFavorites/${id}`, {
          // Use correct API path
          headers: { Authorization: `Bearer ${token}` },
        });
        setIsFavorite(false);
      } else {
        // Add the book to favorites
        await axios.post(
          "http://localhost:3001/bookFavorites", // Use correct endpoint
          {
            bookId: id,
            status: "want-to-read",
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setIsFavorite(true);
      }
    } catch (err) {
      console.error("Error updating favorites:", err);
    }
  };

  if (isLoading) return <h3>Loading Page...</h3>;
  if (!book) return <h3>No Book Found. Please visit homepage and try again</h3>;
  const sanitizedDescription = DOMPurify.sanitize(book.description);

  return (
    <div className="BookDetail">
      <div className="book-card">
        <img
          src={book.imageLinks.thumbnail}
          alt={`Cover of ${book.title}`}
          className="book-card__thumbnail"
        />
        <div className="book-card__details">
          <p>
            <strong>Title:</strong> {book.title}
          </p>
          <p>
            <strong>Author(s):</strong> {book.authors}
          </p>

          <p>
            <strong>Description:</strong>
            <span dangerouslySetInnerHTML={{ __html: sanitizedDescription }} />
            {/* ALL of this extra stuff was to clean up the raw HTML that the API was giving us. It looks cleaner on BookCard cuz its cleaned up by <Link> */}
          </p>

          <p>
            <small>Published: {book.publishedDate}</small>
          </p>
          <p>
            <small>Page Count: {book.pageCount}</small>
          </p>
          <button onClick={handleFavoriteClick}>
            {isFavorite ? "Remove from Favorites" : "Add to Favorites"}
          </button>
        </div>
      </div>
    </div>
  );
}

export default BookDetail;
